
public class mainSinifim {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("sa");
		
		//int örneği:
		int x=60;  //arabanın saatteki hızı x
		System.out.println("Arabanın hızı:"+ x + "km/sa");
		
		//Soru: Arabanın 2 saatte alacağı yolun kaç km olduğunu bulunuz.
		int y = 2*x; // Arabanın 2 saatte alacağı yol
		System.out.println("Arabanın 2 saatte aldığı yol:"+ y + "km");
		

		//short örneği:
		short x2=60;  //arabanın saatteki hızı x
		System.out.println("Arabanın hızı:"+ x2 + "km/sa");
		
		//Soru: Arabanın 2 saatte alacağı yolun kaç km olduğunu bulunuz.
		short y2 = (short)(2*x2); // Arabanın 2 saatte alacağı yol
		System.out.println("Arabanın 2 saatte aldığı yol:"+ y2 + "km");
		
		//long örneği:
		long x3=60;  //arabanın saatteki hızı x
		System.out.println("Arabanın hızı:"+ x3 + "km/sa");
				
		//Soru: Arabanın 2 saatte alacağı yolun kaç km olduğunu bulunuz.
		long y3 = (long)(2*x3); // Arabanın 2 saatte alacağı yol
		System.out.println("Arabanın 2 saatte aldığı yol:"+ y3 + "km");
		
		//float
		float x4=20.4f;
		System.out.println("x4:"+x4);
		
		boolean flag = true;
		
		
	}

}
